CREATE TABLE domaines (id SERIAL UNIQUE, nom character varying(100));

CREATE TABLE ecoles (id SERIAL UNIQUE, nom character varying(50), site character varying(60), description character varying(2000));

CREATE TABLE batiments (id SERIAL UNIQUE, nom character varying(50), type character varying(30), lat float, lng float);

CREATE TABLE formations (id SERIAL UNIQUE, nom character varying(200), niveau character varying(30), id_ecole INTEGER, id_batiment INTEGER, id_domaine INTEGER);
